mapper.insert:
/* RefreshTokenMapper.insertToken [RefreshToken.xml] : Refresh Token 저장 */
SELECT REFRESH_TOKEN_SEQ.NEXTVAL FROM DUAL
INSERT INTO "REFRESH_TOKEN" (SEQ, USER_ID, TOKEN, EXPIRE_DT, REG_DT) VALUES(#{SEQ}, #{USER_ID}, #{TOKEN}, #{EXPIRE_DT}, SYSDATE)

mapper.select:
/* RefreshTokenMapper.findByToken [RefreshToken.xml] : 토큰으로 조회 */

   SELECT SEQ, USER_ID, TOKEN, TO_CHAR(EXPIRE_DT, 'YYYY-MM-DD HH24:MI:SS') AS EXPIRE_DT, TO_CHAR(REG_DT, 'YYYY-MM-DD HH24:MI:SS') AS REG_DT
   FROM "REFRESH_TOKEN"
   WHERE TOKEN = #{token}
     AND EXPIRE_DT > SYSDATE

mapper.delete:
/* RefreshTokenMapper.deleteByUserId [RefreshToken.xml] : 사용자 토큰 전체 삭제 */

   DELETE FROM "REFRESH_TOKEN"
   WHERE USER_ID = #{userId}

/* RefreshTokenMapper.deleteByToken [RefreshToken.xml] : 특정 토큰 삭제 */

   DELETE FROM "REFRESH_TOKEN"
   WHERE TOKEN = #{token}
